const hourHand=document.querySelector(".hour-hand"),minuteHand=document.querySelector(".min-hand"),secondHand=document.querySelector(".second-hand");function setDate(){const now=new Date,hours=now.getHours(),minutes=now.getMinutes(),seconds=now.getSeconds(),hourDegree=hours/12*360,minuteDegree=minutes/60*360,secondDegree=seconds/60*360;hourHand.style.transition=0===hourDegree?"all 0s":"all 0.2s linear",minuteHand.style.transition=0===minuteDegree?"all 0s":"all 0.2s linear",secondHand.style.transition=0===secondDegree?"all 0s":"all 0.2s cubic-bezier(0, 2, 1, 0.6)",hourHand.style.transform=`rotate(${hourDegree}deg)`,minuteHand.style.transform=`rotate(${minuteDegree}deg)`,secondHand.style.transform=`rotate(${secondDegree}deg)`}setInterval(setDate,1e3);