const canvas=document.querySelector("#draw");canvas.width=window.innerWidth-50,canvas.height=window.innerHeight-50;const ctx=canvas.getContext("2d");ctx.lineJoin="round",ctx.lineCap="round",ctx.lineWidth=10;let isDrawing=!1,lastX=0,lastY=0,hue=0;function draw(event){isDrawing&&(ctx.strokeStyle=`hsl(${hue}, 100%, 50%)`,hue+=1,ctx.beginPath(),ctx.moveTo(lastX,lastY),ctx.lineTo(event.offsetX,event.offsetY),ctx.stroke(),[lastX,lastY]=[event.offsetX,event.offsetY])}canvas.addEventListener("mousedown",function(event){isDrawing=!0,[lastX,lastY]=[event.offsetX,event.offsetY]}),canvas.addEventListener("mouseup",function(){isDrawing=!1}),canvas.addEventListener("mouseout",function(){isDrawing=!1}),canvas.addEventListener("mousemove",draw);