const itemsList=document.querySelector(".plates"),addItems=document.querySelector(".add-items"),items=JSON.parse(localStorage.getItem("items"))||[];function addItem(event){event.preventDefault();const text=this.querySelector("[name=item]").value,item={text:text,done:!1};items.push(item),populateList(items,itemsList),localStorage.setItem("items",JSON.stringify(items)),this.reset()}function populateList(plates=[],platesList){platesList.innerHTML=plates.map(function(plate,i){return`\n        <li>\n          <input type="checkbox" data-index=${i} id="item${i}" ${plate.done?"checked":""} />\n          <label for="item${i}">${plate.text}</label>\n        </li>\n      `}).join("")}function toggleDone(event){if(event.target.matches("input")){const element=event.target,index=element.dataset.index;items[index].done=!items[index].done,localStorage.setItem("items",JSON.stringify(items)),populateList(items,itemsList)}}addItems.addEventListener("submit",addItem),itemsList.addEventListener("click",toggleDone),populateList(items,itemsList);