const hero=document.querySelector(".hero"),text=hero.querySelector("h1");function shadow(event){const width=hero.offsetWidth,height=hero.offsetHeight,walk=200;let x=event.offsetX,y=event.offsetY;this!==event.target&&(x+=event.target.offsetLeft,y+=event.target.offsetTop);const xWalk=Math.round(x/width*200-100),yWalk=Math.round(y/height*200-100);text.style.textShadow=`\n    ${xWalk}px ${yWalk}px 0 rgba(255, 0, 255, 0.7),\n    ${-1*xWalk}px ${yWalk}px 0 rgba(0, 255, 255, 0.7),\n    ${yWalk}px ${-1*xWalk}px 0 rgba(0, 255, 0, 0.7),\n    ${-1*yWalk}px ${xWalk}px 0 rgba(0, 0, 255, 0.7)\n  `}hero.addEventListener("mousemove",shadow);