const timeNodes=Array.from(document.querySelectorAll("[data-time]")),seconds=timeNodes.map(function(node){return node.dataset.time}).map(function(timeCode){const timeArr=timeCode.split(":"),mins=parseInt(timeArr[0]),secs=parseInt(timeArr[1]);return 60*mins+secs}).reduce(function(total,vidSeconds){return total+vidSeconds});console.log("Total seconds: "+seconds);const hours=Math.floor(seconds/3600),min=Math.floor(seconds%3600/60),sec=Math.floor(seconds%3600%60);console.log(hours+":"+min+":"+sec);