const video=document.querySelector(".flex"),speed=document.querySelector(".speed"),bar=speed.querySelector(".speed-bar");speed.addEventListener("mousemove",function(event){const y=event.pageY-this.offsetTop,percent=y/this.offsetHeight,min=.4,max=4,height=Math.round(100*percent)+"%",playbackRate=3.6*percent+.4;bar.style.height=height,bar.textContent=playbackRate.toFixed(2)+"x",video.playbackRate=playbackRate});