let countdown;const timerDisplay=document.querySelector(".display__time-left"),endTime=document.querySelector(".display__end-time"),buttons=document.querySelectorAll("[data-time]");function timer(seconds){clearInterval(countdown);const now=Date.now(),then=now+1e3*seconds;displayTimeLeft(seconds),displayEndTime(then),countdown=setInterval(function(){const secondsLeft=Math.round((then-Date.now())/1e3);secondsLeft<0?clearInterval(countdown):displayTimeLeft(secondsLeft)},1e3)}function displayTimeLeft(seconds){const min=Math.floor(seconds%3600/60),sec=Math.floor(seconds%3600%60),display=`${min}:${sec<10?"0":""}${sec}`;document.title=display,timerDisplay.textContent=display}function displayEndTime(timestamp){const end=new Date(timestamp),hour=end.getHours(),minute=end.getMinutes();endTime.textContent=`Be Back At ${hour}:${minute<10?"0":""}${minute}`}function startTimer(){const seconds=parseInt(this.dataset.time);timer(seconds)}buttons.forEach(function(button){button.addEventListener("click",startTimer)}),document.customForm.addEventListener("submit",function(event){event.preventDefault();const mins=this.minutes.value;timer(60*mins),this.reset()});